import React, { useState } from "react";
import { Input, Button, FormItem, FormContainer, Alert } from "components/ui";
import { ActionLink } from "components/shared";
import useTimeOutMessage from "utils/hooks/useTimeOutMessage";
import { Field, Form, Formik } from "formik";
import * as Yup from "yup";
import { forgotPassword } from "service/authService";

const validationSchema = Yup.object().shape({
  email: Yup.string().required("Please enter your email"),
});

const ForgotPasswordForm = (props) => {
  const { disableSubmit = false, className, signInUrl = "/sign-in" } = props;

  const [emailSent, setEmailSent] = useState(false);

  const [message, setMessage] = useTimeOutMessage();

  const onSendMail = async (values, setSubmitting) => {
    const { email } = values;
    setSubmitting(true);
    localStorage.setItem("email", email);
    const formData = new FormData();
    formData.append("email", email);
    // console.log("formData", formData);
    try {
      const resp = await forgotPassword({ email });
      console.log("ðŸš€ ~ onSendMail ~ resp:", resp);
      if (resp.success) {
        setSubmitting(false);
        setEmailSent(true);
      }
    } catch (errors) {
      setMessage(
        "Something went wrong, Please check your email is valid or not!"
      );
      setSubmitting(false);
    }
  };

  return (
    <div className={className}>
      <div className="mb-6">
        {emailSent ? (
          <>
            <h3 className="mb-1">Check your email</h3>
            <p>We have sent a password recovery instruction to your email</p>
          </>
        ) : (
          <>
            <h3 className="mb-1">Forgot Password</h3>
            <p>
              Please enter your email address to receive a verification code
            </p>
          </>
        )}
      </div>
      {message && (
        <Alert className="mb-4" type="danger" showIcon>
          {message}
        </Alert>
      )}
      <Formik
        initialValues={{
          email: "admin@yopmail.com",
        }}
        validationSchema={validationSchema}
        onSubmit={(values, { setSubmitting }) => {
          if (!disableSubmit) {
            onSendMail(values, setSubmitting);
          } else {
            setSubmitting(false);
          }
        }}
      >
        {({ touched, errors, isSubmitting }) => (
          <Form>
            <FormContainer>
              <div className={emailSent ? "hidden" : ""}>
                <FormItem
                  invalid={errors.email && touched.email}
                  errorMessage={errors.email}
                >
                  <Field
                    type="email"
                    autoComplete="off"
                    name="email"
                    placeholder="Email"
                    component={Input}
                  />
                </FormItem>
              </div>
              <Button
                block
                loading={isSubmitting}
                variant="solid"
                type="submit"
              >
                {emailSent ? "Resend Email" : "Send Email"}
              </Button>
              <div className="mt-4 text-center">
                <span>Back to </span>
                <ActionLink to={signInUrl}>Sign in</ActionLink>
              </div>
            </FormContainer>
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default ForgotPasswordForm;
